using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement; public class NextButton : MonoBehaviour {  	//	public static string resetAura = "n";  	GameObject nextObj; 	// Use this for initialization 	void Start () {  	}  	// Update is called once per frame 	void Update () {  	}  	void OnMouseDown(){  		if (Question.count > 4) { 			SceneManager.LoadScene ("ScoreReportScene"); 		} 		if (Question.count == 4) { 			nextObj = GameObject.Find ("Next"); 			nextObj.GetComponent<TextMesh> ().text = "Exit"; 		} 		Question.preventClicking = "n"; 		GameObject selectedObject, correctObject; 		GameObject textObject = GameObject.Find("ResultText"); 		if (Question.correctAnswer [Question.index] == Question.selectedAnswer) { 			selectedObject = GameObject.Find (Question.selectedAnswer); 			selectedObject.GetComponent<TextMesh> ().color = new Color (1, 1, 1); 			textObject.GetComponent<TextMesh> ().text = "Select one of the options";  		}  else { 			selectedObject = GameObject.Find (Question.selectedAnswer); 			correctObject = GameObject.Find (Question.correctAnswer [Question.index]); 			selectedObject.GetComponent<TextMesh> ().color = new Color (1, 1, 1); 			correctObject.GetComponent<TextMesh> ().color = new Color (1, 1, 1); 			textObject.GetComponent<TextMesh> ().text = "Select one of the options";  		} 		Question.nextQuestion = -1; 	} }  